% -------- MOSTRAR TRIANGULACIÓN  ---------%%    Programa para mostrar y guardar%   la triangulación y nodos del MEF%% ------------- PARAMETROS ----------------%%    N: matriz de coord nodales%    T: matriz interconexiones%    nombre: nombre archivo salida%% -----------------------------------------function mostrar_triangulacion(N, T, nombre)    % Grafica la malla    figure(5);    scatter(N(:, 1), N(:, 2), 'filled'); % Dibuja los nodos como puntos    hold on; % Mantén la figura actual para agregar la triangulación    % Dibuja las líneas de la triangulación    for i = 1:size(T, 1)        x_coords = N(T(i, 1:3), 1);        y_coords = N(T(i, 1:3), 2);        x_coords = [x_coords; x_coords(1)]; % Agrega el primer nodo al final para cerrar el triángulo        y_coords = [y_coords; y_coords(1)];        plot(x_coords, y_coords, 'b'); % Dibuja la línea que conecta los nodos del triángulo    end    hold off; % Termina de agregar elementos a la figura    % Configura el aspecto de la gráfica    xlabel('Eje X');    ylabel('Eje Y');    title('Malla y Triangulación');    % Coloca la leyenda debajo del gráfico    legend('Nodos', 'Triangulación', 'Location', 'southoutside');    % Ajusta la resolución    dpi = 1000; % Experimenta con diferentes valores    % Guarda figura    outputFileName = nombre; % Nombre del archivo de salida    print(gcf, outputFileName, '-djpeg', sprintf('-r%d', dpi));end