% --------------- PARAMETROS --------------%% w : longitud en metros de un lado de caja%% -----------------------------------------function analizarErrores_MEF(w=1)    % Vector para almacenar los resultados    resultados = zeros(4, 2);    % Valores de nodos a probar    nodos = [10,15,20,25,30,35,40,45,50];    for i = 1:length(nodos)        n = nodos(i);        % Cálculo de los valores de f y u en los puntos X        f_values = EJ2_SA(w, w, n, n, k_inf=100, V0=1, gr=0);        v_values = EJ2_MEF(w,n,c1=0,c2=1,c3=0,c4=0,tr=0,dat=0, gr=0);        % Cálculo del error absoluto        error_absoluto = abs(v_values - f_values);        % Eliminar casos donde el error es igual a 1        error_absoluto = error_absoluto(error_absoluto ~= 1);        % Calcular el error promedio        error_promedio = mean(error_absoluto);        % Almacenar resultados en el vector        resultados(i, 1) = n;        resultados(i, 2) = error_promedio;    end    % Mostrar los resultados    disp('Resultados:')    disp(resultados)    % Graficar los resultados    figure;    plot(resultados(:, 1), resultados(:, 2), '-o');    title('Error promedio de MEF en función del número de nodos');    xlabel('Número de nodos');    ylabel('Error promedio');    grid on;end