% --------- GUARDAR MATRICES  -----------%%    Programa para guardar matrices%  de triangulaci처n y nodos de MEF%% ------------- PARAMETROS -------------%%    N: matriz de coord nodales%    T: matriz interconexiones%    nombre: nombre archivo salida%% --------------------------------------function guardar_matrices(N,T,nombre);  outputFileName = nombre; % Nombre del archivo de salida  fid = fopen(outputFileName, 'w'); % Abre el archivo para escritura  % Escribe los datos de los nodos en el archivo  fprintf(fid, 'Nodos:\n');  fprintf(fid, 'x\ty\tfrontera\n');  for i = 1:size(N, 1)      fprintf(fid, '%f\t%f\t%d\n', N(i, 1), N(i, 2), N(i, 3));  end  % Escribe los datos de la triangulaci처n en el archivo  fprintf(fid, 'Triangulaci처n:\n');  fprintf(fid, 'N째 Elem\tN1\tN2\tN3\n');  for i = 1:size(T, 1)      fprintf(fid, '%d\t%d\t%d\t%d\n', i, T(i, 1), T(i, 2), T(i, 3));  end  fclose(fid); % Cierra el archivoend